mapper.select:
		select
		
		from cms_article
		
			
				title like "%" #{title} "%"
			
			
				and status =#{status}
			
			
				and user_id =#{userId}
			

			 
				and hot=#{hot}
			
			
			
				and deleted=#{deleted}
			
			
				and channel_Id=#{channelId}
			
			
				and category_Id=#{categoryId}
			
			
				and content_Type=#{contentType}
			
			
		
		
		order by updated desc
